import { useState } from 'react';
import './App.css';

function Form() {
  const[user, setUser] = useState('')
  const[password, setPassword] = useState('')
  const [userlen, setUserlen] = useState(0)
  const submitHandle = (e) => {
    e.preventDefault()

    console.log("User id:" ,user, "  Password:", password)
    setUserlen(user.length)
  }
  const userHandler = (e) => {
    setUser(e.target.value)
    
  }

  const passwordHandler = (e) => {
    setPassword(e.target.value)
  }
 
  return (
    
    <div className="Container" >
        
      <form action="get" className='form' onSubmit={submitHandle}>
        <h2>Login Form</h2>
        <label htmlFor="" >User Name</label><br />
        <span>{userlen>3?<small>enetr write value</small>:<small>all good</small>}</span>
        <input type="text" onChange={userHandler} /><br />
        <label htmlFor="" >Password</label><br />
        <input type="password" onChange={passwordHandler} /><br />
        <button className='btn'>Submit</button>
        {/* <h3>{userlen}</h3> */}
      </form>
    </div>
  );
}

export default Form;
