import { useState } from 'react';
import './App.css';

function Form() {
  const formValue = {username:"", password:""}
  const[userdata, setUserdata] = useState(formValue)
  
  const submitHandle = (e) => {
    e.preventDefault()
    console.log("User id:" ,formValue.username, "  Password:",formValue.password)
  }

  const handleChange = (e)=>{
    console.log(e.target.value)
    setUserdata({...formValue, [userdata]:formValue.username})
  }
 
 
  return (
    
    <div className="Container" >
        
      <form action="get" className='form' onSubmit={submitHandle}>
        <h2>Login Form</h2>
        <label htmlFor="username" >User Name</label><br />
        <input type="text" name='username' value={formValue.username} onChange={handleChange}/><br />
        <label htmlFor="password" >Password</label><br />
        <input type="password" name='password' value={formValue.password} onChange={handleChange}/><br />
        <button className='btn' type='submit' >Submit</button>
        {/* <h3>{userlen}</h3> */}
      </form>
    </div>
  );
}

export default Form;
